FROM botiga
JOIN empleat USING (botiga_id)
JOIN comanda ON empleat_id = repartidor_id
JOIN comanda_has_producte USING (comanda_id)
JOIN producte p USING (producte_id)
WHERE p.tipo = 'begudes'
GROUP BY botiga_id;

